import React,{useState,useContext} from 'react';
import useStyles from './styles';
import { ExpenseTrackerContext } from '../../../context/Context';
import {v4 as uuidv4 } from 'uuid';
import { TextField, Typography, Grid, Button, FormControl, InputLabel, Select, MenuItem } from '@material-ui/core'
import { incomeCategories,expenseCategories } from '../../../constants/categories';
const initialState =
{
    amount: '',
    category: '',
    type: 'Income',
    date : new Date(),
}



const Form = () => {
    const classes = useStyles();
     const [formData, setFormData] = useState(initialState)
     const {addTransaction} =useContext(ExpenseTrackerContext);
     const createTransaction = () =>
     {
        const transaction={...formData,amount: Number(formData.amount),id: uuidv4()};
        addTransaction(transaction);
        setFormData(initialState);
     }

     const selectedCategories = incomeCategories = formData.type === 'Income' ? incomeCategories : expenseCategories;
    return (
        <Grid container spacing={2}>
            <Grid item xs={12}>
                <Typography align="center" variant="subtitle2" gutterBottom>
                </Typography>
            </Grid>
            <Grid item xs={6}>
                <FormControl fullWidth>
                    <InputLabel>Type</InputLabel>
                    <Select value={formData.type} onChange={(e) => setFormData({ ...formData, type: e.target.value })}>
                        <MenuItem value="Income">Income</MenuItem>
                        <MenuItem value="Expense">Expense</MenuItem>
                    </Select>
                </FormControl>
            </Grid>
            <Grid item xs={6}>
                <FormControl fullWidth>
                    <InputLabel>Category</InputLabel>
                    <Select value={formData.category} onChange={(e) => setFormData({ ...formData, category: e.target.value })}>
                        {selectedCategories.map((c) => <MenuItem key ={c.type} value={c.type}></MenuItem>)}
                    </Select>
                </FormControl>
            </Grid>

            <Grid item xs={6}>
                <TextField type="number" label="Amount" value={formData.amount} onChange={(e) => setFormData({ ...formData, amount: e.target.value })} fullWidth  />
            </Grid>
            <Grid item xs={6}>
                <TextField fullWidth label="Date" type="date" value={formData.date} onChange={(e) => setFormData({ ...formData, date: e.target.value })} />
            </Grid>
            <Button fullWidth className={classes.button} variant="outlined" color="primary" onClick={createTransaction} >Create</Button>
        </Grid>
    )
}

export default Form
